wikitext.*:
/*搜索框，请在参数设置中打开整合至个人工具*/
#p-search {
    position: fixed;
    bottom: 60px;
    left: 40px;
}
div#simpleSearch {
    border: 0;
    background: #fff;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.2);
    border-radius: 3px;
    transition: all 0.3s ease-out;
    opacity: 0;
}
div#simpleSearch:hover {
    opacity: 1;
}
:not(textarea):not(code):not(pre):not(input),
#firstHeading {
font-family: "Roboto Slab", "Source Han Sans", "HanaMinA Regular", "HanaMinB Regular", "TH-Khaai-TP0", "TH-Khaai-TP1", "TH-Khaai-TP2";
}
pre,
code,
tt,
kbd,
samp,
.mw-code,
textarea,
input {
font-family: Menlo, Monaco, Consolas, "Andale Mono", "lucida console", "Courier New", "Source Han Sans", "HanaMinA Regular", "HanaMinB Regular", "TH-Khaai-TP0", "TH-Khaai-TP1", "TH-Khaai-TP2", monospace;
}
/*黑幕模糊化，改自https://userstyles.org/styles/118277/bangumi-mask*/
.heimu,
.heimu a {
    background-color: transparent !important;
    filter: blur(5px);
    -webkit-filter: blur(5px);
    -moz-filter: blur(5px);
    -o-filter: blur(5px);
    transition: all 200ms 300ms;
}
.heimu a {
    color: #1976D2 !important;
}
.heimu:hover {
    filter: blur(0.2px);
    -webkit-filter: blur(0.2px);
    -moz-filter: blur(0.2px);
    -o-filter: blur(0.2px);
    color: black !important;
}
.heimu a:hover {
    filter: blur(0.2px);
    -webkit-filter: blur(0.2px);
    -moz-filter: blur(0.2px);
    -o-filter: blur(0.2px);
    color: #1976D2 !important;
}
/*选中背景色*/

::selection {
    background: #ADD8E6;
}
/*顶端*/
.topButton1, .topButton2 {
    position: absolute;
    width: 30px;
    top: 20px;
    transition: all 300ms ease;
}
.topButton1:hover, .topButton2:hover {
    opacity: .8;
}
.topButton1 {
    left: 20px;
}
.topButton2 {
    right: 20px;
}
#mw-head-base {
    margin-top: -5em;
    margin-left: 0 !important;
    height: 5em;
    box-shadow: 0 140px 0 200px #03A9F4;
    background: #03A9F4;
    width: 100%;
}
div#p-personal {
    width: 100%;
    position: fixed;
    top: 0;
    right: 0;
    padding: 12px !important;
    background: #03A9F4;
    box-shadow: 0 1px 3px 0 rgba(0, 34, 77, .05);
    border-bottom: 1px solid rgba(30, 35, 42, .06);
    justify-content: space-between;
}
#p-personal ul {
    float: right;
    color: rgba(255,255,255,0.95);
    background: transparent;
    transition: all 0.3s ease;
    opacity: 0;
}
#p-personal ul:hover {
    opacity: 1;
}
#p-personal a {
    color: rgba(255,255,255,0.95);
    font-family: "Source Han Sans", sans-serif;
}
#pt-notifications-alert .mw-echo-notifications-badge,
#pt-notifications-notice .mw-echo-notifications-badge {
    color: transparent !important;
}
.topTitle {
    color: rgba(255,255,255,0.95);
    float: left;
    font-size: 22px;
    position: absolute;
    left: 18%;
    line-height: 32px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    font-family: "方正宋黑简体", sans-serif;
    max-width: 20em;
}
/*主体*/
@-webkit-keyframes main
{
    from {margin-top: 180px; opacity: 0;}
    to {margin-top: 100px; opacity: 1;}
}
.ns-0 div#content,
.ns-0 div#p-cactions li a:hover,
.ns-0 div#p-cactions li.selected a,
.ns-0 div#content div.thumb,
div#content,
div#p-cactions li a:hover,
div#p-cactions li.selected a,
div#content div.thumb {
    background: #fff;
}

#content {
    animation: main 500ms;
    background: #fff;
    margin-left: auto;
    margin-right: auto;
    margin-top: 100px;
    width: 70%;
    border: none;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
    border-radius: 3px;
}
.mw-body h1, .mw-body h2,
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: "方正宋黑简体", serif;
    color: rgba(0, 0, 0, 0.87);
}
#firstHeading {
    font-family: "方正宋黑简体", serif;
    position: absolute;
    top: 80px;
    left: 0;
    color: rgba(255,255,255,0.95);
    width: 100%;
    text-align: center;
    transition: all 300ms ease;
    padding-left: 0;
    display: inline !important;
}
#firstHeading:hover {
    opacity: .8;
}
span.subpages {
    margin-left: 10px;
}
.mw-body-content {
    font-family: "Source Han Sans", sans-serif;
    color: rgba(0, 0, 0, 0.7);
}
#mw-page-base, body.skin-vector {
    background: #f6f6f6;
}
a,
.mw-body-content a.extiw,
.mw-body-content a.extiw:active,
.mw-body-content a.external {
    color: #1976D2;
    text-decoration: none !important;
    transition: color 0.3s ease;
}
a:visited,
.mw-body-content a.external:visited,
.mw-body-content a.extiw:visited {
    color: #5E35B1;
}
a:active,
.mw-body-content a.external:active,
.mw-body-content a.extiw:active,
a:hover,
.mw-body-content a.external:hover,
.mw-body-content a.extiw:hover {
    color: #FFC107;
}
h1,
h2 {
    border-bottom: none;
}
.comment-user a,
.comment-user a:visited {
    color: #777;
}
#toc,
.toc {
    background-color: #f8f9fa;
    border: 0;
    border-radius: 3px;
    padding: 8px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
#catlinks {
    border: 0;
    border-radius: 3px;
}
#wpSave,
#wpPreview,
#wpDiff {
    font-family: inherit;
    font-size: 1em;
    display: inline-block;
    min-width: 4em;
    max-width: 28.75em;
    padding: 0.546875em 1em;
    line-height: 1.286;
    margin: 0;
    border-radius: 2px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-appearance: none;
    zoom: 1;
    vertical-align: middle;
    background-color: #f8f9fa;
    color: #222222;
    border: 1px solid #a2a9b1;
    text-align: center;
    font-weight: bold;
    cursor: pointer;
}
.infoBox {
    border: 0 !important;
    border-radius: 3px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
.infoBoxContent {
    background: #ECEFF1;
    line-height: 1.3;
    border: 0 !important;
    border-radius: 3px;
}
.footer-info a,
div#footer ul li a,
div#footer ul li {
    color: rgba(0, 0, 0, 0.7);
}
html,
body,
body.skin-vector,
.comment-body,
.comment-body textarea,
.comment-preview,
.comment-submit,
.footer-info a,
div#footer ul li a,
div#footer ul li,
span[lang=ja]{
    font-family: "Source Han Sans", sans-serif;
}
hr {
    background: #ccc;
}
@media (max-width: 1116px) {
    #p-personal ul,
    #p-personal a {
        visibility: hidden;
    }
}
.wikiEditor-ui-tabs,
.wikiEditor-ui-tabs div {
    border: 0;
}
.wikiEditor-ui-tabs div.current {
    background: #E0EEF7;
}
.wikiEditor-ui-toolbar {
    background-image: url();
}
.wikiEditor-ui .wikiEditor-ui-top {
    border: 0;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
.wikiEditor-ui .wikiEditor-ui-view {
    border: 0;
}
.editOptions {
    border: 0;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
/*表格相关*/

table.wikitable > tr > th,
table.wikitable > tr > td,
table.wikitable > * > tr > th,
table.wikitable > * > tr > td {
    border: none;
}
table.wikitable {
    border: none;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
    border-radius: 3px;
    color: rgba(0, 0, 0, 0.7);
}
table.wikitable > tr > th,
table.wikitable > * > tr > th {
    background-color: #eaecf0;
}

/*navbox*/

table.navbox {
    border: none;
    font-size: smaller;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
/*infobox*/

.infotemplatebox,
.acg-works-information {
    border: none !important;
    border-radius: 3px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
/*滚动条*/

::-webkit-scrollbar {
    width: 6px;
    height: 10px;
    background-color: rgba(0, 0, 0, 0);
}
::-webkit-scrollbar-thumb {
    border-radius: 3px;
    background-color: rgba(0, 0, 0, 0.2);
    transition: all 0.4s ease;
    -moz-transition: all 0.4s ease;
    -webkit-transition: all 0.4s ease;
    -o-transition: all 0.4s ease;
}
::-webkit-scrollbar-thumb:hover {
    background-color: rgba(0, 0, 0, 0.3);
}
::-webkit-scrollbar-track {
    background-color: rgba(0, 0, 0, 0.1);
}
/*隐藏内容*/

#localNotice,
#footer-info-copyright,
.mw-editTools,
#mw-panel,
#left-navigation,
#right-navigation,
.mw-body .mw-editsection,
.mw-body .mw-editsection-like,
#footer-icons {
    display: none;
}
