wikitext.*:
$(function() {
    var img = $('<img>').attr('src', '//commons.moegirl.org/extensions/Avatar/avatar.php?user=' + mw.config.get('wgUserName'));
    var link = $('<a>').attr('href', '//commons.moegirl.org/Special:UploadAvatar').append(img);
    $('#pt-userpage').before($('<li id="pt-avatar"></li>').append(link));
    if (mw.config.get("wgNamespaceNumber") === 2 && !mw.config.get("wgPageName").includes("/")) {
        $('.ns-2 #firstHeading').prepend(
            $('<a>').attr({
                href: 'https://commons.moegirl.org/Special:Viewavatar?user=' + mw.config.get("wgPageName").replace(/^user:/i, ''),
                title: '查看头像'
            }).prepend(
                $('<img>')
                .attr('src', '//commons.moegirl.org/extensions/Avatar/avatar.php?user=' + mw.config.get("wgPageName").replace(/^user:/i, ''))
                .css({
                    width: '1.2em',
                    height: '1.2em',
                    'border-radius': '10px',
                    padding: '4px'
                })
            )
        );
    }
});
