// <pre>
(function() {
    function useravatar() {
        $('.mw-userlink:not(.user-avatar-added)').each(function() {
            var item = $(this);
            var img = $('<img/>').on('error', function() {
                window.setTimeout(function() {
                    img.closest('.userlink-avatar').remove();
                }, 0);
            }).addClass('userlink-avatar-small').attr('src', '//commons.moegirl.org/extensions/Avatar/avatar.php?user=' + item.text());
            item.prepend($('<span/>').addClass('userlink-avatar').append(img));
            item.addClass("user-avatar-added");
        });
        if (mw.config.exists('user-avatar-magnifier')) mw.config.get('user-avatar-magnifier')();
    }
    setInterval(function() { if (mw.config.exists('user-avatar-magnifier')) mw.config.get('user-avatar-magnifier')(); }, 5000);
    mw.config.set('useravatar', useravatar);
    $(useravatar);
    $(window).on('load', useravatar);
})();
