/* JS placed here will affect widget editors only */
/* 函数执行体 */
$(function() {
    //Widget预加载模板
    function toUpperFirstCase(t) {
        return t[0].substring(0, 1).toUpperCase() + t.substring(1);
    }
    if (!(mw.config.get('wgArticleId') == 0 && mw.config.get('wgCanonicalNamespace') == 'Widget' && mw.config.get('wgAction') == 'edit')) return;
    if (mw.config.get('wgTitle').indexOf('-') !== -1) location.replace(mw.config.get('wgTitle').replace(/ /g, '').split('-').map(function(n, i) { if (i) return toUpperFirstCase(n); return n;}).join(''));
    var flag = ("wg" + mw.config.get('wgTitle')).replace(/ /g, '');
    if (mw.config.get('wgArticleId') == 0 && mw.config.get('wgCanonicalNamespace') == 'Widget' && mw.config.get('wgAction') == 'edit') $("#wpTextbox1").val("<noinclude> </noinclude><includeonly><!--{if !isset($" + flag + ") || !$" + flag + '}--><!--{assign var="' + flag + '" value=true scope="global"}--><script>\nwindow.RLQ = window.RLQ || [];\nwindow.RLQ.push(function(){\n    mw.loader.implement("widget' + mw.config.get('wgTitle') + '",function($, jQuery){\n\n    });\n});</script><!--{/if}--></includeonly>');
});
