wikitext.*:
/* 放置于这里的CSS将影响使用移动版网站的用户 */
/* 广告 */
.banner-container {
	overflow: hidden;
}
/* 黑幕 */
.heimu, .heimu a, a .heimu, .heimu a.new {
    background-color: #252525;
    color: #252525;
    text-shadow: none;
}
.heimu:hover, .heimu:active,
.heimu:hover .heimu, .heimu:active .heimu {
    color: white !important;
}
.heimu:hover a, a:hover .heimu,
.heimu:active a, a:active .heimu {
    color: lightblue !important;
}
.heimu:hover .new, .heimu .new:hover, .new:hover .heimu,
.heimu:active .new, .heimu .new:active, .new:active .heimu {
    color: #BA0000 !important;
}
/* 修复单元格莫名空行 */
td {
    vertical-align: middle;
}
/* 修复子页面列表和common-box宽度不足 */
.mw-prefixindex-list-table,
.common-box {
    width: 100%!important;
}
/* 侧边栏和替换logo图片隐藏 */
#sidebar-character,
#wglogo {
    display: none;
}
/* 导航条 */
#breadcrumb {
    font: bold 18px Arial, Helvetica, sans-serif;
    color: black;
    border: solid 1px rgb(204, 204, 204);
    width: 100%;
    overflow: hidden;
    margin: 0;
    padding: 0 0 .5em 0;
}
#breadcrumb span {
    list-style-type: none;
    float: left;
    padding-left: 10px;
}
#breadcrumb span:after {
    content: "·"
}
#breadcrumb span:last-child:after {
    content: "";
}
#breadcrumb a {
    text-align: center;
    vertical-align: middle;
    display: inline-block;
    padding-right: 15px;
    text-decoration: none;
    color: #454545!important;
}
.home {
    border: none;
    margin: 8px 0;
}
/* 注释文字 */
sup.mobileref {
    display:none;
}
sup.mobileref:after {
    content: " (" attr(title) ")";
    display: inline;
}
/* 首页适配 */
.page-Main_Page .gallery div,
.page-Main_Page .gallery div img,
.page-Main_Page .gallery p {
    position: initial !important;
    display: inline;
    margin-bottom: 7px;
}
.page-Main_Page .mainpage-title {
    border-top: 1px solid rgb(204, 204, 204);
    border-bottom: 1px solid rgb(204, 204, 204);
    text-align: center;
    font-size: larger;
    font-family: Arial, Tahoma, Microsoft YaHei, \5fae\8f6f\96c5\9ed1, Hiragino Sans GB, Microsoft JhengHei, 微軟正黑體, sans-serif;
    margin: 7px;
}
/* 页顶提示模板相关 */
#commonBoxInfo {
    color: #f00;
    font-weight: bold;
    text-align: center;
}
#commonBoxList > .commonBoxButton {
    display: inline-block;
    border-bottom: 3px solid #000;
    width: 40px;
    height: 40px;
    background: #fff 50% 50% no-repeat;
    background-size: contain;
    cursor: pointer;
    transition: opacity .5s;
}
#commonBoxList.open > .commonBoxButton {
    opacity: .1;
}
#commonBoxList.open > .commonBoxButton.current {
    opacity: 1;
}
/* infoBox */
.infoBox {
    margin: 0 auto;
    clear: both;
}
.infoBoxTitle {
    margin: 3px auto;
    padding: 0;
    text-align: center;
    font-weight: bold;
}
.infoBoxIcon {
    display: table-cell;
    padding: 2px 0 2px 0.5em;
    vertical-align: middle;
}
@media screen and (min-width: 640px) {
    .content .infoBoxIcon a > img {
        max-width: none!important;
    }
}
.infoBoxText {
    display: table-cell;
    padding: 0.25em 0.5em 0.25em 1.3em;
    width: 100%;
    vertical-align: middle;
}
.infoBoxContent {
    border: 1px solid #ddd;
    border-left-width: 0;
    background: #fbfbfb;
}
.infoBoxBelow {
    margin: 0 auto;
    padding: 0;
    text-align: center;
}
/* code 内边距修复 */
code {
    padding: 3px
}
/* Tabs */
.tab .TabLabelText {
    display: inline-block;
    padding: 1px;
    cursor: pointer;
}
.tab .TabLabel {
    z-index: 2;
    position: sticky;
    top: 0;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -o-user-select: none;
    user-select: none;
}
.tab .TabLabelText.selected {
    border-top: 3px solid green;
    padding: 1px;
    background: white;
    border-left: 1px solid #aaa;
    border-right: 1px solid #aaa;
}
.tab .TabContent {
    background: white;
    padding: 20px 30px;
    position: relative;
    z-index: 1;
    border: 1px solid #aaa;
}
.tab .TabContentText {
    display: none;
}
.tab .TabContentText.selected {
    display: block;
}
.Tabs {
    position: relative;
}
.Tabs .Tab {
    border-bottom: 1px solid #aaa;
}
.Tabs .Tab:first-child {
    border-top: 1px solid #aaa;
}
.Tabs .Tab .TabLabelText {
    padding: 7px;
}
.Tabs .Tab .TabContentText {
    padding: 3px;
}
/* wikitable green */
table.wikitable-green {
    background: #c9f6e1
}
.wikitable-green th {
    background: #13ec93
}
.wikitable-green td {
    background: #f3f3f3
}
/* wikitable border */
#content.content table.wikitable {
    border: none;
}
/* 大家族模板 */
.navbox th, .navbox-group {
    background: #a5e4a5;
}
/* 来个高优先级的 */
body .content .navbox {
    display: table!important;
}
.navbox .content table {
    width: 100% !important;
}
.navbox {
    border: 1px solid #aaa;
    clear: both;
    padding: 1px;
    text-align: center;
    width: 100%;
}
.navbox-group {
    white-space: nowrap;
}
.navbox table {
    width: 100% !important;
    margin: 0 !important;
}
.navbox-subgroup .navbox-group {
    background: #e6f6e6;
}
.navbox-title > div:first-of-type {
    display: none;
}
/* Fix by case : Rev 1327309 ([[Special:Permalink/1327309]])
    Address alignment issue of nested tabs in Navbox */
.navbox .TabLabel {
    text-align: left;
}
@media screen and (max-width: 640px) {
    .navbox-group {
        display: block;
    }
    .navbox-group + * {
        display: block;
        width: 100%;
        border-left: 1em solid #a5e4a5 !important;
        padding-left: 3px !important;
        padding-top: 3px !important;
        box-sizing: border-box;
    }
    .navbox-subgroup .navbox-group + * {
        border-left: 1em solid #e6f6e6 !important;
    }
}
/* 修复用户页欢迎辞覆盖编辑框的bug */
.overlay.visible {
    z-index: 999;
}
/* Fix by case: Tracklist doesn't display on mobile devices due to inappropriate margin configuration  */
.tracklist {
    margin:0 !important;
}
/* 链接提示 */
.linkConfirmprompt {
    border: 1px solid black;
    display: none;
    padding: 3px 7px;
    position: absolute;
    text-align: center;
    width: 300px;
    z-index: 99999;
    background: rgb(255, 255, 255);
}
.linkConfirmpromptTextnode {
    max-height: 2.5em;
    text-align: left;
    word-break: break-all;
}
.linkConfirmprompt a {
    margin: 0 1.5em;
    cursor: pointer;
    color: #3366cc;
}
/*{{Infobox Ooarai team}、{{GUP人物信息}}移动版CSS。*/
.Infobox_GUP {
    text-align: left;
    font-size: small;
    line-height: 1.5em;
    border: 1px solid #a2a9b1;
    background-color: #FFFFFF;
    float: right;
}
.GUP_Title_Cell {
    text-align: center;
    padding: 0.25em 0.33em 0.33em;
    line-height: 1.2em;
    font-size: 1.25em;
    background-color: #FF9999;
    color: #FFFFFF;
}
.GUP_Subtitle_Cell {
    text-align: center;
    background-color: #FF9999;
    color: #FFFFFF;
    font-size: 100%;
    padding: 0.438em 0.625em;
}
.GUP_Item_Title_Cell {
    width: 5em;
    background-color: #FFCCCC;
    white-space: nowrap;
    padding: 0.438em 0.625em;
    vertical-align: top;
    text-align: left;
}
.Infobox_GUP tr {
    border: 1px solid #a2a9b1;
}
.Infobox_GUP td {
    vertical-align: top;
    padding: 0.438em 0.625em;
}
