wikitext.*:
.gallery-mod {
	background: transparent;
	margin-top: 0.5em;
}

.gallery-mod-collapsible {
	width: 100%;
}

.gallery-mod-center {
	margin: 0 auto;
}

.gallery-mod-title {
	text-align: center;
	font-weight: bold;
}

.gallery-mod-box {
	float: left;
	border-collapse: collapse;
	margin: 3px;
}

.gallery-mod-box .thumb {
	border: 1px solid #ccc;
	background-color: #F8F8F8;
	padding: 0;
	text-align: center;
}

tr.gallery-mod-text {
	vertical-align: top;
}

tr.gallery-mod-text .core {
	display: block;
	font-size: small;
	padding: 0;
}

.gallery-mod-text .caption {
	line-height: 1.25em;
	padding: 6px 6px 1px 6px;
	margin: 0;
	border: none;
	border-width: 0;
	text-align: left;
}

.gallery-mod-footer {
	text-align: right;
	font-size: 80%;
	line-height: 1em;
}
