/* hover状态切换 */
.hover-change,
.hover-remote{
  position: relative;
  display: inline-block;
  transition: opacity 0.5s linear;
}

.hover-change-before,
.hover-change-after,
.hover-remote-target{
	transition: inherit;
}

.hover-change-after,
.hover-remote-target{
	opacity: 0;
	position: absolute;
	top: 0;
	left: 0;
}
.hover-remote-target{
  left: 100%;
}

.hover-change-after[style^="right:"],
.hover-change-after[style*=" right:"],
.hover-remote-after[style^="right:"],
.hover-remote-after[style*=" right:"]{
  left: initial;
}

.hover-change-after[style^="bottom:"],
.hover-change-after[style*=" bottom:"],
.hover-remote-after[style^="bottom:"],
.hover-remote-after[style*=" bottom:"]{
  top: initial;
}

.hover-change-after:hover,
.hover-remote-target{
	opacity: 0;
	margin-left: -9999px;
}

/* 闪烁效果 */
.hover-change.flash:hover .hover-change-before {
    margin-left: -9999px;
}

.hover-change:hover > .hover-change-before,
.hover-remote > .hover-remote-target{
	opacity: 0;
}

.hover-change:hover > .hover-change-after,
.hover-remote:hover > .hover-remote-target{
	opacity: 1;
	margin-left: initial;
}

