/* 基类 */
.hover {
  display: inline-block;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0);
  transform: perspective(1px) translateZ(0);
  vertical-align: middle;
  transition-duration: 0.25s;
  transition-property: all;
}

/* 旋转放大 */
.hover.rotate-shrink{
	transition-duration: 0.5s;
	transform: rotate(0) scale(0.75);
}

.hover.rotate-shrink:hover{
	transform: rotate(360deg) scale(1);
}

/* 正常 => 浅 */
.hover.fade-deep,
.hover.fade,
.hover.fade-shallow {
  opacity: 1;
}
.hover.fade-deep:hover, .hover.fade-deep.active {
  opacity: 0.75;
}
.hover.fade:hover, .hover.fade.active {
  opacity: 0.5;
}
.hover.fade-shallow:hover, .hover.fade-shallow.active {
  opacity: 0.35;
}

/* 浅 => 正常 */
.hover.bloom-deep {
  opacity: 0.35;
}
.hover.bloom {
  opacity: 0.5;
}
.hover.bloom-shallow {
  opacity: 0.75;
}

.hover.bloom-deep:hover, .hover.bloom-deep.active,
.hover.bloom:hover, .hover.bloom.active,
.hover.bloom-shallow:hover, .hover.bloom-shallow.active {
  opacity: 1;
}

/* 正常 => 大 */
.hover.grow-deep,
.hover.grow,
.hover.grow-shallow {
  transform: scale(1);
}
.hover.grow-deep:hover, .hover.grow-deep.active {
  transform: scale(1.5);
}
.hover.grow:hover, .hover.grow.active {
  transform: scale(1.25);
}
.hover.grow-shallow:hover, .hover.grow-shallow.active {
  transform: scale(1.1);
}

/* 正常 => 小 */
.hover.shrink-deep:hover, .hover.shrink-deep.active,
.hover.shrink:hover, .hover.shrink.active,
.hover.shrink-shallow:hover, .hover.shrink-shallow.active {
  transform: scale(1);
}
.hover.shrink-deep {
  transform: scale(0.5);
}
.hover.shrink {
  transform: scale(0.75);
}
.hover.shrink-shallow {
  transform: scale(0.9);
}

