  right: 0;
}

.hover.sweep-to-left .content{
  left: 100%;
}
.hover.sweep-to-left:hover .content, .hover.sweep-to-left.active .content{
  left: 0;
}

.hover.sweep-to-bottom .content{
  top: initial;
  bottom: 100%;
}
.hover.sweep-to-bottom:hover .content, .hover.sweep-to-bottom.active .content{
  bottom: 0;
}

.hover.sweep-to-top .content{
  top: 100%;
}
.hover.sweep-to-top:hover .content , .hover.sweep-to-top.active .content {
  top: 0;
}

.hover.radial-out .content,
.hover.radial-in:hover .content, .hover.radial-in.active .content{
  opacity: 0;
}

.hover.radial-in .content,
.hover.radial-out:hover .content, .hover.radial-out.active .content{
  opacity: 1;
}

/* 默认em */
.hover.border,
.hover.trim,
.hover.ripple-out,
.hover.ripple-in,
.hover.outline-out,
.hover.outline-in,
.hover.reveal{
	font-size: 16px;
}

/* 内边框滑出 */
.hover.border,
.hover.trim{
  position: relative;
  top: 0;
  left: 0;
  transition: all 0.3s;
}

.hover.border:before{
  content: '';
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  box-sizing: border-box;
  transition: inherit;
  border-width: calc(0.625em * 0.6);
  border-color: inherit;
  border-style: solid;
  border-radius: inherit;
  opacity: 0;
}

.hover.border:hover:before, .hover.border.active:before{
  opacity: 1;
}

/* 带边距内边框 */
.hover.trim:before{
  content: '';
  position: absolute;
  top: calc(0.625em * 0.4);
  left: calc(0.625em * 0.4);
  bottom: calc(0.625em * 0.4);
  right: calc(0.625em * 0.4);
  transition: inherit;
  border-width: calc(0.625em * 0.6);
  border-color: inherit; 
  border-style: solid;
  opacity: 0;
  border-radius: inherit;
}

.hover.trim:hover:before, .hover.trim.active:before{
  opacity: 1;
}

/* 边框飞出与飞入 */
.hover.ripple-out,
.hover.ripple-in,
.hover.outline-out,
.hover.outline-in,
.hover.reveal{
  animation-duration: 0.7s;
  animation-timing-function: ease-out;
  transition: all 0.3s;	
}

.hover.ripple-out:before,
.hover.ripple-in:before,
.hover.outline-out:before,
.hover.outline-in:before,
.hover.reveal:before {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  animation: inherit;
  transition: inherit;
  border-width: calc(0.625em * 0.6);
  border-color: inherit;
  border-style: solid;
  border-radius: inherit;
}
.hover.ripple-out:before{
	opacity: 0;
}
.hover.ripple-in:before{
  top: calc(-0.625em * 1.2);
  right: calc(-0.625em * 1.2);
  bottom: calc(-0.625em * 1.2);
  left: calc(-0.625em * 1.2);
  opacity: 0;
}
.hover.outline-out:before{
  opacity: 0;
}
.hover.outline-out:before{
  top: calc(-0.625em * 1.6);
  right: calc(-0.625em * 1.6);
  bottom: calc(-0.625em * 1.6);
  left: calc(-0.625em * 1.6);
  opacity: 0;
}
.hover.reveal:before{
  border-width: 0;
  opacity: 0;
}


@keyframes ripple-out {
  50%{
  	opacity: 1;
  }

  100% {
    top: calc(-0.625em * 1.2);
    right: calc(-0.625em * 1.2);
    bottom: calc(-0.625em * 1.2);
    left: calc(-0.625em * 1.2);
    opacity: 0;
  }
}
.hover.ripple-out:hover:before , .hover.ripple-out.active:before {
  animation-name: ripple-out;
}

@keyframes ripple-in {
  50%{
    opacity: 1;
  }

  100% {
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    opacity: 0;
  }
}
.hover.ripple-in:hover:before , .hover.ripple-in.active:before {
  animation-name: ripple-in;
}

.hover.outline-out:hover:before,
.hover.outline-in:hover:before , .hover.outline-out.active:before,
.hover.outline-in:hover:before {
  top: calc(-0.625em * 0.8);
  right: calc(-0.625em * 0.8);
  bottom: calc(-0.625em * 0.8);
  left: calc(-0.625em * 0.8);
  opacity: 1;
}

.hover.reveal:hover:before , .hover.reveal.active:before {
  transform: translateY(0);
  border-width: calc(0.625em * 0.4);
  opacity: 1;
}

/* 圆角化 */
.hover.round-corners{
  overflow: hidden;
}
.hover.round-corners:hover, .hover.round-corners.active{
  border-radius: 0.625em;
}
