wikitext.*:
.infobox2{
	border-spacing: 3px;
	text-align: left;font-size: small;
	line-height: 1.5em;
	border: 1px solid #a2a9b1;
	padding: 0.2em;
	float: right;    
	background-color: #FFFFFF;
}
.ib2_title
{
	text-align:center;
	background-color:#26ca9b; 
	color:#FFFFFF; 
	font-size:100%; 
	padding:1em;
}
.ib2_subtitle{
	text-align:center;
	background-color:#deede0;
}
.infobox2 td, .infobox2 th {
    vertical-align: top;
}
@media screen and (max-device-width: 720px) {
    table.infobox2 th, table.infobox2 td {
        vertical-align: top;
        border: 0;
        border-bottom: 1px solid #eaecf0;
        padding: 7px 10px;
    }
}
