wikitext.*:
.Lyrics.Lyrics-has-ruby .Lyrics-original,
.Lyrics.Lyrics-has-ruby .Lyrics-translated {
	line-height: 2.1;
}
.Lyrics.Lyrics-no-ruby .Lyrics-original,
.Lyrics.Lyrics-no-ruby .Lyrics-translated {
	vertical-align: top;
}
.Lyrics .Lyrics-original,
.Lyrics .Lyrics-translated {
	width: 100%;
	display: inline-block;
	white-space: pre-wrap;
}
@media all and ( max-width: 720px ) {
	.Lyrics {
		min-width: 100%;
	}
}

@media all and ( min-width: 720px ) {
	.Lyrics .Lyrics-original,
	.Lyrics .Lyrics-translated {
		width: 49.85%;
	}
}
