wikitext.*:
a,
pre,
code,
.noripple,
.heimu,
.hover {
    position: relative !important;
}

.mouse-ripple {
    width: 100%;
    height: 100%;
    position: fixed;
    top: 0;
    left: 0;
}

.mouse-ripple b {
    display: block;
}

.mouse-ripple .a {
    height: 2vw;
}

.mouse-ripple .a .b {
    width: 2vw;
    height: 2vw;
    display: inline-block;
    overflow: hidden;
    opacity: 0.7;
}

.mouse-ripple .a .b>b {
    height: 100%;
    border-radius: 50%;
    overflow: hidden;
    transition: all 0.5s;
    transform: scale(0);
    opacity: 0;
}

.mouse-ripple .a .b>b::before {
    content: '';
    width: 100%;
    height: 100%;
    display: block;
    box-sizing: border-box;
    border-color: inherit;
    border: 1vw solid;
}

.mouse-ripple .a .b:hover b {
    transition-duration: 0s;
    transform: scale(1);
    opacity: 1;
}
