wikitext.*:
@media all and (max-width:720px) {
	table.multicol > tr > td,
	table.multicol > tbody > tr > td {
		display:block !important;
		width:auto !important;
	}
}
table.multicol {
	border: 0;
	border-collapse: collapse;
	background-color: transparent;
	padding: 0;
	table-layout: fixed;
}
table.multicol > tr > td,
table.multicol > tbody > tr > td {
	vertical-align: top;
}
