wikitext.*:
/* Styling for Template:Stack */
@media all and (max-width: 720px) {
    body.skin-minerva .mw-stack {
		float: none !important;
		width: 100% !important;
	}
}
.stack-container {
	box-sizing: border-box;
}
.stack-clear-left {
	float: left;
	clear: left;
}
.stack-clear-right {
	float: right;
	clear: right;
}
.stack-left {
	float: left;
}
.stack-right {
	float: right;
}
.stack-object {
	margin: 1px;
	overflow: hidden;
}
