wikitext.*:
@keyframes toright{
  0% {
    left: 100%;
  }
  100% {
    left: -50em;
  }
}

@keyframes hide{
  0% {
    opacity: 1;
  }
  99.9% {
  	opacity: 1;
  }
  100% {
    opacity: 0;
  }
}

.danmaku{
  position: fixed;
  left: 100%;
  white-space: nowrap;
  z-index: 1000;
  line-height: 1;
}
