wikitext.*:
.audio-btn{
	position: relative;
	display: inline-block;
}

.audio-btn > .sm2-player-body > div[data-bind]:only-child{
	width: 100%;
	height: 100%;
	position: absolute;
	top: 0;
	left: 0;
	cursor: pointer;
	opacity: 0;
}

.audio-btn > .sm2-player-body .sm2-bar-ui.sm2-bar-ui-lite{
	min-width: initial;
	max-width: initial;
	min-height: initial;
	max-height: initial;
}

.audio-btn > .sm2-player-body [class^="sm2-widget-"],
.audio-btn > .sm2-player-body .sm2-bar-ui.sm2-bar-ui-lite,
.audio-btn > .sm2-player-body .bd.sm2-main-controls,
.audio-btn > .sm2-player-body .sm2-inline-element.sm2-button-element{
	width: 100%;
	height: 100%;
}
