/* WikiPlus的颜色调整 */
body div.output-warning {
    color: #6dcc88;
}
body div.output-error{
    color: #ff9000;
}
div#content a:visited {
    color: #3366BB;
}
/* 这里的代码能使WikiPlus的按钮样式更modern…… */
div#wikiplus-main-button {
    box-shadow:0 0 0px #ffffff!important;
}
div#wikiplus-function {
    box-shadow:0 0 0px #ffffff!important;
} 
textarea#quickedit {
    box-shadow:0 0 0px #ffffff!important;
}
div#wikiplus-function li {
    box-shadow:0 0 0px #ffffff!important;
}
div#wikiplus-quickedit-summary-input {
    box-shadow:0 0 0px #ffffff!important;
}
div.wikiplus-btn {
    box-shadow:0 0 0px #ffffff!important;
}
