#hitokoto-toggleSetting {
    cursor: pointer;
}

#hitokoto-toggleSetting:hover {
    text-decoration: underline;
}

#hitokoto.transition #hitokoto-setting {
    transition: 0.37s all ease;
}

#hitokoto.hide #hitokoto-setting {
    height: 0;
    overflow: hidden;
}

#hitokoto.show #hitokoto-setting {
    height: 517px;
    overflow: hidden;
}

#hitokoto-scroll {
    overflow-y: scroll;
    height: 400px;
    padding: 0;
    position: relative;
}

#hitokoto-scroll .hitokotoTableHead {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
}

#hitokoto-scroll table {
    margin: 0!important;
}

#hitokoto-setting {
    background: transparent;
}

#hitokoto-setting table {
    width: 100%;
}

#hitokoto span,
#hitokoto td {
    text-align: left;
    word-wrap: break-word;
    word-break: break-all;
}

#hitokoto-scroll .hitokotoTableContent thead {
    opacity: 0;
}

#hitokoto-scroll .hitokotoTableContent thead th {
    background: none;
    border: none;
}

#hitokoto input[type="text"] {
    margin-right: 3em;
}

