.AnnTools_Frame {
    position: fixed;
    z-index: 999;
    top: 13%;
    left: 17%;
    height: 74%;
    width: 66%;
    background: white;
    border: 1px solid #a7d7f9;
    display: none
}

.AnnTools_Frame_Head {
    height: 20%;
    position: relative
}

.AnnTools_Frame_Title {
    border-bottom: 1px solid gray;
    padding: 13px
}

.AnnTools_Frame_Close {
    padding: 7px;
    cursor: pointer;
    font-size: 37px;
    position: absolute;
    top: -10%;
    right: 0
}

.AnnTools_Frame_Close.disable {
    cursor: not-allowed;
    opacity: .3
}

.AnnTools_Frame.Content {
    height: 80%;
    clear: both
}

.AnnTools_Confirm {
    position: fixed;
    top: 37%;
    left: 37%;
    width: 26%;
    border: 1px solid #a7d7f9
}

.AnnTools_Confirm_Content,
.AnnTools_Frame_Content {
    padding: 1em
}

.AnnTools_Confirm_Yes,
.AnnTools_Confirm_No {
    cursor: pointer;
    box-sizing: border-box;
    width: 36%;
    margin: 1em 7%;
    border: 1px solid #a7d7f9;
    padding: 1em 0;
    text-align: center;
    display: inline-block
}

.AnnTools_floatAlert {
    display: none;
    background: white;
    border: 1px solid #a7d7f9;
    padding: 1em;
    width: 17em
}

.AnnTools_Info {
    display: none
}

.AnnTools_ProgressBar {
    margin: 13px auto;
    padding: 3px;
    height: 1em;
    width: 73%;
    border: 1px solid #a7d7f9
}

.AnnTools_ProgressBar_Finished {
    background: green;
    height: 100%;
    transition: .73s all ease;
    width: 0
}

.AnnTools_Notice {
    font-size: 17px;
    text-align: center
}

.AnnTools_ProgressBar.error .AnnTools_ProgressBar_Finished {
    background: red
}

.AnnTools_WorkDetail {
    list-style-type: decimal
}

.AnnTools_WorkDetail_Ongoing {
    color: orange
}

.AnnTools_WorkDetail_Succeed {
    color: green
}

.AnnTools_WorkDetail_Failed {
    color: red
}
