(function(mw) {
    mw.loader.implement('AnnToolsUploadLogToggle', function($, jQuery) {
        if (!$('.mw-logline-upload')[0]) return false;
        var $uploadLog = $('.mw-logline-upload'),
            ClickToHide, ClickToHideButton;
        if (mw.config.get('wgUserLanguage').indexOf('zh') != -1) ClickToHide = "点此隐藏/显示上传日志：", ClickToHideButton = ['隐藏', '显示'];
        else ClickToHide = "Click this button to hide/show the upload log:", ClickToHideButton = ['Hide', 'Show'];
        ClickToHide += '<input id="toggleUploadLog"/>';
        $('#mw-content-text > form > fieldset').append('<p>' + ClickToHide + '</p>');
        $('#toggleUploadLog').attr('type', 'button').css('margin', '0 0.5em').val(ClickToHideButton[0]).on('click', function() {
            if ($uploadLog.first().css('display') != 'none') {
                $uploadLog.fadeOut();
                $(this).val(ClickToHideButton[1]);
            } else {
                $uploadLog.fadeIn();
                $(this).val(ClickToHideButton[0]);
            }
        });
    });
})(mediaWiki);
