wikitext.*:
#mainpage {
	display: block;
	width: 100%;
	box-shadow: rgba(0, 0, 0, 0.5) 0 0 .75em;
	border-collapse: collapse;
	text-align: left;
}
#mainpage-a-hello {
	width: 66%;
	float: left;
}
#main-page-a-new {
	width: 33%;
	float: left;
}
.mainpage-box {
	float: left;
}
.mainpage-title{
	font-family: EasonPro,"Times New Roman",serif;
	font-size: 1.618em;
	color: white;
	margin: 0px;
	text-shadow: rgba(0, 0, 0, 0.75) 0 0 .25em;
	background: #707070;
	background: -o-linear-gradient(top,rgba(0, 0, 0, .2) 0,transparent 5%,transparent 97%,rgba(0, 0, 0, .45) 100%),-o-linear-gradient(left,rgba(0, 0, 0, .2) 0,transparent .2%,transparent 99.8%,rgba(0, 0, 0, .2) 100%),url('http://img.moegirl.org/skin/globalheader.png') no-repeat 50% 50%,-o-linear-gradient(bottom,#666 0,#5E5E5E 50%,#707070 51%,gray 100%);
	background: -moz-linear-gradient(top,rgba(0, 0, 0, .2) 0,transparent 5%,transparent 97%,rgba(0, 0, 0, .45) 100%),-moz-linear-gradient(left,rgba(0, 0, 0, .2) 0,transparent .2%,transparent 99.8%,rgba(0, 0, 0, .2) 100%),url('http://img.moegirl.org/skin/globalheader.png') no-repeat 50% 50%,-moz-linear-gradient(bottom,#666 0,#5E5E5E 50%,#707070 51%,gray 100%);
	background: -webkit-gradient(linear,0 0,0 100%,from(rgba(0, 0, 0, .2)),color-stop(0.05,transparent),color-stop(0.97,transparent),to(rgba(0, 0, 0, .45))),-webkit-gradient(linear,0 0,100% 0,from(rgba(0, 0, 0, .2)),color-stop(0.002,transparent),color-stop(0.998,transparent),to(rgba(0, 0, 0, .2))),url('http://img.moegirl.org/skin/globalheader.png') no-repeat 50% 50%,-webkit-gradient(linear,0 100%,0 0,from(#666),color-stop(0.5,#5E5E5E),color-stop(0.51,#707070),to(gray));
	font-weight: 400;
	height: 30px;
	line-height: 30px;
	text-align: center;
	font-size-adjust: 0.43;
}
.mainpage-content {
	vertical-align: top;
	padding: .5em 2%;
	border-right: #AAA solid 1px;
	background-color: white;
	filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0,StartColorStr=#ffffffff,EndColorStr=#ffeeeeee);
	background-image: linear-gradient(top,#BBB 0%,white 2%,#EEE 100%);
	background-image: -o-linear-gradient(top,#BBB 0%,white 2%,#EEE 100%);
	background-image: -moz-linear-gradient(top,#BBB 0%,white 2%,#EEE 100%);
	background-image: -webkit-linear-gradient(top,#BBB 0%,white 2%,#EEE 100%);
	background-image: -ms-linear-gradient(top,#BBB 0%,white 2%,white 100%);
	background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#BBB),color-stop(.02,white),color-stop(1,#EEE));
}
.floatleft {
	margin: 0 .5em .5em 0;
	border: 0;
	position: relative;
	float: left;
	clear: left;
}
.clear-div{
	clear: both;
}
