wikitext.*:
/* This is a dirty hack */
body {
	display: none;
}

/* General interface font family selection */
[lang] {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

body {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

body.skin-vector {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

.skin-vector {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

/* Override Heading */
#firstHeading {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB","Microsoft YaHei UI", sans-serif;
}

.mw-body h1, .mw-body h2 {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
	font-weight: 200;
}

/* Content */
p {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

/* Language-specific */
p:lang(ja){
	font-family: "Segoe WP", "Segoe UI", "Meiryo UI", "Hiragino Sans GB", "Source Han Sans", "Microsoft YaHei UI", sans-serif;
}

p:lang(ja-JP) {
	font-family: "Segoe WP", "Segoe UI", "Meiryo UI", "Hiragino Sans GB", "Source Han Sans", "Microsoft YaHei UI", sans-serif;
}

#simpleSearch input {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

button {
	font-family: "Segoe WP", "Segoe UI", "Source Han Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei UI", sans-serif;
}

/* Code blocks */
code {
    font-family: Consolas, Monaco, monospace;
}

pre {
	font-family: Consolas, Monaco, monospace;
}

/* Monaco tokenizer debug */
.token.bold {
	font-weight: bold;
}

.token.italic {
	font-style: italic;
}

/* To be used with custom search */
#MSCustomSearchFrame {
	width: 100%;
	height: 800px;
	border: none;
}
