function rollbackEverythingButton() {
  var hasRollback = getElementsByClassName(document, "span", "mw-rollback-link");
  if (hasRollback[0] && (document.title.indexOf(wgULS("用户贡献","用戶貢獻")) != -1) ) {
    addPortletLink('p-cactions', 'javascript:rollbackEverything()', wgULS("批量回退","批量回退"), "ca-rollbackeverything",  wgULS("回退本页的所有编辑","回退本頁的所有編輯"));
  }
}
addOnloadHook(rollbackEverythingButton);
function rollbackEverything() {
    for (var i in document.links) {
      if (document.links[i].href.indexOf('action=rollback') != -1) {
        window.open(document.links[i].href);
      }
    }
}
