wikitext.*:
$(function () {
    document.onmousemove = function (e) {
    	$('.backToTop').css('transition', 'right 0.37s ease-in-out 0s');
        if (e.clientX > Math.min(document.body.clientWidth * 7 / 8, document.body.clientWidth - 100)) {
            $('.HideRuby').css('right', '0px');
            $('#heimu_toggle').css('right', '0px');
            $('.backToTop').css('right', '0px');
        } else {
            $('.HideRuby').css('right', '-17px');
            $('#heimu_toggle').css('right', '-17px');
            $('.backToTop').css('right', '-17px');
        }
    };
});
