$(function(){
    var replaceArray = { '「' : '“', '」' : '”', '『' : '‘', '』' : '’' };
    var replaceDom = function(dom){
        if (dom.lang == "ja") return;
        if (dom.nodeType == 3) {
            for (var replaceWord in replaceArray) {
                dom.textContent = dom.textContent.replace(new RegExp(replaceWord, 'g'), replaceArray[replaceWord]);
            }
        } else if (dom.nodeType == 1 && dom.nodeName.toLowerCase() != 'textarea' && dom.nodeName.toLowerCase() != 'pre') {
            for (var i = 0; i < dom.childNodes.length; i++) {
                replaceDom(dom.childNodes[i]);
            }
        }
    };
    replaceDom(document.body);
});
