wikitext.*:
body, .mw-body {
	font-family: "Source Han Sans SC", "Source Han Sans CN", "Noto Sans CJK SC", "Microsoft YaHei", sans-serif;
}

#firstHeading, .firstHeading, .portal, .mw-headline, .mainpage-title {
	font-family: "Georgia", "Times", "Source Han Serif SC", "SimSun", serif;
}

.ztdh .ztdh-title {
	font-family: inherit !important;
}

.mw-body span[lang] {
	font-feature-settings: "locl" on;
	-webkit-font-feature-settings: "locl" on;
}
.mw-body [lang=zh-hans], .mw-body [lang=zh-CN] {
	font-family: "Source Han Sans SC", "Source Han Sans CN", "Noto Sans CJK SC", "DengXian", "Microsoft YaHei", "SimHei", sans-serif;
}
.firstHeading [lang=zh-hans], .mw-headline [lang=zh-hans], .firstHeading [lang=zh-CN], .mw-headline [lang=zh-CN] {
	font-family: "Source Han Serif SC", "Source Han Serif CN", "Noto Serif CJK SC", "SimSun", serif;
}
.mw-body [lang=zh-hant], .mw-body [lang=zh-TW] {
	font-family: "Source Han Sans TC", "Source Han Sans TW", "Noto Sans CJK TC", "Microsoft JhengHei", sans-serif;
}
.firstHeading [lang=zh-hant], .mw-headline [lang=zh-hant], .firstHeading [lang=zh-TW], .mw-headline [lang=zh-TW] {
	font-family: "Source Han Serif TC", "Source Han Serif TW", "Noto Serif CJK TC", "MingLiU", serif;
}
.mw-body [lang=zh-HK] {
	font-family: "Source Han Sans HC", "Source Han Sans HK", "Noto Sans CJK TC", "Microsoft JhengHei", sans-serif;
}
.firstHeading [lang=zh-HK], .mw-headline [lang=zh-HK] {
	font-family: "Source Han Serif HC", "Source Han Serif HK", "Noto Serif CJK TC", "MingLiU_HKSCS", serif;
}
.mw-body [lang=ja], .mw-body [lang=ja-JP] {
	font-family: "Source Han Sans", "Source Han Sans JP", "Noto Sans CJK JP", "Yu Gothic", "Meiryo", "MS Gothic", sans-serif;
}
.firstHeading [lang=ja], .mw-headline [lang=ja], .firstHeading [lang=ja-JP], .mw-headline [lang=ja-JP] {
	font-family: "Source Han Serif", "Source Han Serif JP", "Noto Serif CJK JP", "Yu Mincho", "MS Mincho", serif;
}
.mw-body [lang=ko], .mw-body [lang=ko-KR] {
	font-family: "Source Han Sans K", "Source Han Sans KR", "Noto Sans CJK KR", "Malgun Gothic", "Dotum", sans-serif;
}
.firstHeading [lang=ko], .mw-headline [lang=ko], .firstHeading [lang=ko-KR], .mw-headline [lang=ko-KR]{
	font-family: "Source Han Serif K", "Source Han Serif KR", "Noto Serif CJK KR", "Batang", serif;
}

pre, code, tt, kbd, samp, .mw-code, .diff-lineno, .diff-addedline, .diff-deletedline, .diff-context, .wikitext-editor, .mw-plusminus-neg, .mw-plusminus-null, .mw-plusminus-pos {
	font-family: "Sarasa Term SC", "Source Han Sans HW SC", "Courier New", "Courier", monospace;
}

div#mw-panel div.portal div.body ul li a:visited, .mw-body-content a.extiw:visited, .mw-body a.external:visited, a:visited {
	color: darkorange;
}

i, em, var, address, dfn, span.comment, .allpagesredirect {
	margin-right: 0 !important;
	font-style: normal;
	transform: skewX(-10deg);
	display: inline-block;
}

.mw-body li {
	list-style-image: none;
}

.comment-bannotice {
	display: none;
}

a.Wikiplus-Edit-EveryWhereBtn {
	white-space: nowrap;
}
