/* 在新窗口打开连接 */
hrefList=document.getElementsByTagName("a");
for(i=0;i<hrefList.length;i++){
	if(hrefList[i].getAttribute("href")
	&&//这个标签有链接
	hrefList[i].getAttribute("href").charAt(0)!="#"
	&&//这个标签不是页内跳转
	hrefList[i].innerHTML!="编辑"
	//这个标签不是编辑按钮
	){
		hrefList[i].setAttribute("target","_blank");
	}
}
