wikitext.*:
{{#forargs:row | rownum | rowvalue |
    <div style="display:table-row;">
      <div style="display:table-cell;text-align:{{{row{{#var:rownum}}align|center}}};">
        <div style="display:table;width:100%;">
          <div style="display:table-row;background-color:{{#if:{{{row{{#var:rownum}}bgcolor}}}|{{{row{{#var:rownum}}bgcolor}}}|}}">
{{#forargs:in{{#var:rownum}}- | cellnum | cellvalue |
            <div style="display:table-cell;width:{{#expr:100 div {{{in{{#var:rownum}}cells|1}}}}}%;text-align:center;border-top:{{#ifeq:{{#var:rownum}}|1|1|0.5}}px solid {{{border-color|{{{border-color-t|#ccc}}}}}};border-bottom:{{#ifeq:{{#var:rownum}}|{{{length|1}}}|1|0.5}}px solid {{{border-color|{{{border-color-b|#ccc}}}}}};border-left:{{#ifeq:{{#var:cellnum}}|1|1|0.5}}px solid {{{border-color|{{{border-color-l|#ccc}}}}}};border-right:{{#ifeq:{{#var:cellnum}}|{{{in{{#var:rownum}}cells|0}}}|1|0.5}}px solid {{{border-color|{{{border-color-r|#ccc}}}}}};vertical-align:middle;">{{#var:cellvalue}}</div>
}}
           </div>
        </div>
      </div>
    </div>
 }}
