@media screen and (min-width: 720px) {/*过滤小屏幕手机*/

.skin-vector div#p-personal {
    background-color:rgba(255,255,255,0.8);
    padding:5px 10px 5px 0;
    box-shadow:#666 0 1px 5px;
    position:fixed;
    width:100% !important;
    left:95%;
    transition: all .15s 1s;
    height: 28px;
    display:flex;
}
@media screen and (min-width: 1024px) {
  .skin-vector div#p-personal:hover {
      position: fixed;
      left:55%;
      transition: all 0.08s;
  }
}
@media screen and (max-width: 1024px) {
  .skin-vector div#p-personal:hover {
      position: fixed;
      left:25%;
      transition: all 0.08s;
  }
}
.skin-vector div#p-personal:before {
    content: '展开';
    color:gray;
    font-weight: bold;
    display: flex !important;
}
.skin-vector div#p-personal:hover:before {
    content: '';
}
.skin-vector div#p-personal ul {
	padding-left:3em;
}
#right-navigation {
    position: fixed;
    right: -25%;
    border-bottom:1px solid aliceblue;
    background: aliceblue;
    transition: all 0.4s 1s;
}
#right-navigation:hover {
    right: 0%;
    transition: all 0.14s;
}
.skin-vector #mw-panel {
    position: fixed !important;
    top:50px !important;
    overflow: auto !important;
    height:100% !important;
}
/*END*/
}
