wikitext.*:
/* 2017春季topbar */
body.skin-vector {
    background: url(//img.moegirl.org/topbar/zhMoegirl17spring.jpg) no-repeat top left #f6f6f6;
    font-family: Arial, Tahoma, "Microsoft YaHei", \5fae\8f6f\96c5\9ed1, "Hiragino Sans GB", "Microsoft JhengHei", 微軟正黑體, sans-serif;
}


#content {
    width: auto !important;
    box-shadow:0px 0px 20px 0px #ccc;
    border-radius: 10px;
  }



  a{
    -moz-transition:all 0.3s !important;
    -webkit-transition:all 0.3s !important;
    -o-transition:all 0.3s !important;
    transition:all 0.3s !important;
    text-decoration:none !important;
    border-radius:3px;
  }

  a:hover{
    color:#000 !important;

  }




::-webkit-scrollbar-thumb {     
    background-color: #666;     
    height: 50px;     
    outline-offset: -2px;     
    outline: 2px solid #fff;     
    -webkit-border-radius: 4px;     
    border: 2px solid #fff;     
}     

::-webkit-scrollbar-thumb:hover {     
    background-color: #666;     
    height: 50px;     
    -webkit-border-radius: 4px;     
}     


::-webkit-scrollbar {     
    width: 10px;     
    height: 10px;     
}     

::-webkit-scrollbar-track-piece {     
    background-color: #FFF;     
    -webkit-border-radius: 0;     
}

form{margin:0px auto 0px auto;}
itextarea{border:1px solid #000000;background-color:#ffffff;font-size:10px;color:#000000;}
hr{border-top:none;border-bottom:1px solid #000000;border-left:none;border-right:none;}
div#waku{width:700px;border:none;margin:0px auto 0px auto;padding:0px;text-align:center;}
.ue{border:none;padding:0px 20px 0px 20px;margin:0px auto;text-align:left;}
.shita{border:none;padding:0px;text-align:right;font-size:11px;}
.article{padding:20px;clear:both;text-align:left;font-size:10pt;}
.date{color:#000000;font-size:11px;text-align:right;margin-top:0px;}
.title{color:#000000;font-size:11pt;}
.button{color:#000000;background-color:#ffffff;border:1px solid #000000;}
.link{margin:30px auto 30px auto;padding:15px;border:none;font-size:10px;}
.smoji{font-size:10px;}
.cate{padding:0px;margin:0px auto 0px auto;}


select {border:1px solid #000000;background-color:#ffffff;font-size:10px;color:#000000;border-radius: 5px;}
input {border:1px solid #000000;background-color:#ffffff;font-size:9.5px;color:#000000;border-radius: 5px;padding: 5px;}

.mainpage-content,
.mainpage-1stcontent,
.mainpage-title {
    border-left: #DEEDE0 solid 1px;
    border-right: #DEEDE0 solid 0px;
}
.mainpage-title {
    border-top: #DEEDE0 solid 1px;
    margin-right: -1px;
}
div.mainpage-content.nomobile {
    border-bottom: #DEEDE0 solid 1px;
}
div#mainpage {
    border-right: #DEEDE0 solid 1px;
}

.mw-mmv-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0,0,0,0.8);
}

.navpopup {
  border: 1px solid #bbb;
  background-color: #fff;
  padding: 10px;
  padding-bottom: 5px;
  font-size: 11px;
  box-shadow: 0 3px 8px rgba( 50, 50, 50, 0.35 );
}
