/* Classes for large group of texts */

.cm-mw-link-text {
	color: #36b;
}

.cm-mw-link-trail {
	color: #36b;
}

.cm-mw-template-arg {
	color: darkred;
}

.cm-mw-template-argname {
	color: darkgoldenrod;
}

.cm-mw-pre, .CodeMirror pre.cm-mw-pre {
	color: #555;
    background-color: rgba(0, 0, 0, 0.05);
}

/* Classes for symbols */

.cm-mw-extlink {
	color: #36b;
}

.cm-mw-hr {
	color: #08f;
	font-weight: bold;
}

.cm-mw-table-start {
	color: #e0e;
	font-weight: bold;
}

.cm-mw-ident {
	color: #219;
	font-weight: bold;
}

.cm-mw-magic-word {
	color: #666;
    font-weight: bold;
}

.cm-mw-signature {
	color: #f00;
	font-weight: bold;
}

.cm-mw-isbn {
	color: #36b;
}

.cm-mw-rfc {
	color: #36b;
}

.cm-mw-pmid {
	color: #36b;
}

.cm-mw-section {
	color: #219;
	font-weight: bold;
}

/*
.cm-mw-section-1 {
   	font-size: 1.8em !important;
}

.cm-mw-section-2 {
   	font-size: 1.5em !important;
}

.cm-mw-section-3 {
   	font-size: 1.2em !important;
}
*/

.cm-mw-bold-start, .cm-mw-bold-end {
	color: #219;
	font-weight: bold;
}

.cm-mw-italic-start, .cm-mw-italic-end {
	color: #219;
	font-style: italic;
}

.cm-mw-bolditalic-start, .cm-mw-bolditalic-end {
	color: #219;
	font-style: italic;
	font-weight: bold;
}

.cm-mw-comment {
	color: green;
}

.cm-mw-tag-open {
	color: purple;
}

.cm-mw-tag-close {
	color: purple;
}

.cm-mw-tag-attr {
	color: brown;
}

.cm-mw-entity {
	color: purple;
}

.cm-mw-link-start, .cm-mw-link-pipe, .cm-mw-link-end {
	color: #36b;
	font-weight: bold;
}

.cm-mw-link-target {
	color: #36b;
	font-weight: bold;
	text-decoration: underline;
}

.cm-mw-extlink-start, .cm-mw-extlink-end {
	color: #36b;
	font-weight: bold;
}

.cm-mw-extlink-target {
	color: #36b;
	font-weight: bold;
	text-decoration: underline;
}

.cm-mw-template-start, .cm-mw-template-pipe, .cm-mw-template-end {
	color: darkred;
	font-weight: bold;
}

.cm-mw-template-assign {
	color: darkgoldenrod;
	font-weight: bold;
}

.cm-mw-template-name {
	color: darkred;
	font-weight: bold;
	text-decoration: underline;
}

/* Fix WikiEditor popup */
.wikiEditor-ui-toolbar {
	z-index: 10;
}
