/*黑幕模糊化，改自https://userstyles.org/styles/118277/bangumi-mask*/
.heimu,
.heimu a{
    background-color: transparent !important;
    color: #252525 !important;
    border: none !important;
    filter: blur(3px);
    transition: filter 100ms 200ms;
}

.heimu:hover,
.heimu a:hover {
    filter: blur(0.2px);
}

/*选中背景色*/
::selection {
    background: #ADD8E6;
}

/*顶端*/
#p-personal {
    width: 100%;
    text-align: right;
    position: fixed;
    top: 0;
    right: 0;
    padding: 8px 0 !important;
}

#p-personal ul {
    float: right;
}
